payload.query:
subscription configuration($input: SubscribeInput!) {
  subscribe(input: $input) {
    id
    ... on BasicMessage {
      data {
        __typename
        ... on ConfigurationMessageData {
          colorPalette {
            colors {
              hex
              index
              __typename
            }
            __typename
          }
          canvasConfigurations {
            index
            dx
            dy
            __typename
          }
          canvasWidth
          canvasHeight
          __typename
        }
      }
      __typename
    }
    __typename
  }
}
